package com.biz.var;

public class Variable01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 변수 선언과 관련된 keyword들
		// 자료형
		
		// 숫자형
		int intNum1 = 0 ; // 정수형
		long longNum1 = 0l ; // 배 용량 정수형
		short shNum1 = 0 ; // 반용량 정수형
		
		float floatNum1 = 0.0f ; // 실수형
		double douNum1 = 0.0 ; 
		
		// 문자형
		char charAlpah = 'A' ; // 영숫자 특수문자 1글자 (작은따옴표)
		char charNum = '0' ; 
		char charSpec = '&' ;
		char charSpace = ' ' ; 
		
		// 문자열형
		String strName = "홍길동" ; // 영숫자 특수문자 (큰따옴표) 
		String strTel = "123-4456" ;
		String strAddr = "광주광역시 북구 중흥동" ;
		
		// 불린형 : 참, 거짓 자료형
		boolean isTrue = true ;
		boolean isFalse = false ;
	}

}
